<?phpnamespace App\Filament\Admin\Resources\BuilderPageResource\RelationManagers;use App\Enums\Builder\EnumsBlockTemplate;use App\Enums\Builder\EnumsBlockType;use Filament\Forms;use Filament\Forms\Form;use Filament\Resources\RelationManagers\RelationManager;use Filament\Tables;use Filament\Tables\Table;class BlocksRelationManager extends RelationManager {  protected static string $relationship = 'blocks';  public function form(Form $form): Form {    return $form      ->schema([        Forms\Components\Toggle::make('is_active')          ->label('نشط')          ->default(true),      ]);  }  public function table(Table $table): Table {    $thisLang = app()->getLocale();    return $table      ->modifyQueryUsing(fn ($query) => $query->with('template'))      ->recordTitleAttribute('display_name')      ->columns([        Tables\Columns\TextColumn::make('name.' . $thisLang)          ->label(__('default/lang.columns.name')),        Tables\Columns\TextColumn::make('template.template')          ->label(__('builder/builder-block-template.columns.template'))          ->formatStateUsing(fn ($state) => EnumsBlockTemplate::tryFrom($state)?->label())          ->sortable()          ->searchable(),        Tables\Columns\TextColumn::make('template.type')          ->label(__('builder/builder-block-template.columns.type'))          ->formatStateUsing(fn ($state) => EnumsBlockType::tryFrom($state)?->label())          ->sortable()          ->searchable(),        Tables\Columns\IconColumn::make('pivot.is_active')          ->label(__('default/lang.columns.is_active'))          ->boolean()      ])      ->filters([        //      ])      ->headerActions([      ])      ->actions([        Tables\Actions\EditAction::make(),      ])      ->bulkActions([      ])      ->defaultSort('builder_page_pivot.position')      ->reorderable('builder_page_pivot.position');  }}