<?phpnamespace App\Filament\Admin\Resources\BuilderPageResource\RelationManagers;use Filament\Forms;use Filament\Forms\Form;use Filament\Resources\RelationManagers\RelationManager;use Filament\Tables;use Filament\Tables\Table;use Illuminate\Contracts\Support\Htmlable;use Illuminate\Database\Eloquent\Builder;use Illuminate\Database\Eloquent\Model;use Illuminate\Database\Eloquent\SoftDeletingScope;class BlocksRelationManager extends RelationManager {  protected static string $relationship = 'blocks';//  public static function canCreate(): bool {//    return false;//  }  public function form(Form $form): Form {    return $form      ->schema([        Forms\Components\Toggle::make('is_active')          ->label('نشط')          ->default(true),      ]);  }  public function table(Table $table): Table {    return $table      ->recordTitleAttribute('display_name')      ->columns([        Tables\Columns\TextColumn::make('name')->label('اسم البلوك'),        Tables\Columns\TextColumn::make('type')->label('النوع'),        Tables\Columns\IconColumn::make('pivot.is_active')          ->label('نشط')          ->boolean()      ])      ->filters([        //      ])      ->headerActions([//        Tables\Actions\CreateAction::make(),      ])      ->actions([        Tables\Actions\EditAction::make(),//        Tables\Actions\DeleteAction::make(),      ])      ->bulkActions([      ])      ->defaultSort('builder_page_pivot.position')      ->reorderable('builder_page_pivot.position');  }}